mapper.select:
select *
from
(
    select rownum as RNUM, A.*
    from
    (
        select * from book 

        order by no desc
    )A
)
where RNUM > #{firstRecordIndex} 

and RNUM <= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
update book
set title = #{title}, price = #{price}, publisher = #{publisher}
where no = #{no}
